mapper.update:
update classroom_resource
set res_room_status = #{roomStatus}
where room_num = #{roomNum}
  and res_date >= #{nowTime}
  and apply_info_id is null;

update classroom_resource
set res_room_status = "已预订"
where room_num = #{roomNum}
  and res_date >= #{nowTime}
  and apply_info_id is not Null;

update classroom_resource
set res_room_status= #{roomStatus}
where room_num = #{roomNum}
  and res_date between #{nowTime} and #{duration}
