mapper.select:
select count(*)
from apply_info
where apply_book_time = CURRENT_DATE;

SELECT users.user_name, users.user_phone, apply_book_time, building_name, classroom.room_num, res_date, res_week, res_day_of_week, res_period, apply_purpose, apply_person_count, apply_status from users, apply_info, classroom_resource, classroom, building, college where users.user_account = apply_info.user_account and apply_info.apply_id = classroom_resource.apply_info_id and classroom_resource.room_num = classroom.room_num and classroom.building_id = building.building_id and users.college_id = college.college_id
AND apply_info.apply_status = #{apply_status}
AND users.college_id = #{college_id}
AND building.building_id = #{building_id}
AND users.user_name LIKE CONCAT('%', #{user_name}, '%')
AND classroom_resource.res_date BETWEEN #{date_start} AND #{date_end}
AND classroom_resource.res_date >= #{date_start}
AND classroom_resource.res_date <= #{date_end}
