mapper.select:
SELECT u.user_name, u.user_phone as phone, ai.apply_book_time as book_time, b.building_name, c.room_num, cr.res_date as date, cr.res_week as week, cr.res_day_of_week as day_of_week, cr.res_period as period, ai.apply_purpose as purpose, ai.apply_person_count as person_count, ai.apply_status FROM apply_info ai LEFT JOIN users u ON ai.user_account = u.user_account LEFT JOIN classroom_resource cr ON ai.apply_id = cr.apply_info_id LEFT JOIN classroom c ON cr.room_num = c.room_num LEFT JOIN building b ON c.building_id = b.building_id WHERE ai.sec_account = #{secAccount} AND ai.user_cancel = 0
AND ai.apply_status = #{applyStatus}
AND c.building_id = #{buildingId}
AND u.user_name LIKE CONCAT('%', #{userName}, '%')
AND cr.res_date >= #{dateStart}
AND cr.res_date <= #{dateEnd}
ORDER BY ai.apply_book_time DESC

mapper.update:
UPDATE apply_info
SET apply_status = #{applyStatus}

    ,apply_reject_reason = #{rejectReason}

WHERE apply_id = #{applyId}
    AND user_cancel = 0

mapper.select:
SELECT 
    CONCAT(ROUND(
        (SELECT COUNT(*) FROM classroom_resource cr1
         LEFT JOIN classroom c1 ON cr1.room_num = c1.room_num
         WHERE cr1.res_room_status = '已预订'

             AND c1.building_id = #{buildingId}


             AND c1.room_type = #{roomType}

        ) * 100.0 / 
        (SELECT COUNT(*) FROM classroom_resource cr2
         LEFT JOIN classroom c2 ON cr2.room_num = c2.room_num
         WHERE 1=1

             AND c2.building_id = #{buildingId}


             AND c2.room_type = #{roomType}

        ), 2), '%'
    ) as avg_usage_rate,

    (SELECT CONCAT(b.building_name, '-', cr.room_num)
     FROM classroom_resource cr
     LEFT JOIN classroom c ON cr.room_num = c.room_num
     LEFT JOIN building b ON c.building_id = b.building_id
     WHERE cr.res_room_status = '已预订'

         AND c.building_id = #{buildingId}


         AND c.room_type = #{roomType}

     GROUP BY cr.room_num
     ORDER BY COUNT(*) DESC
     LIMIT 1
    ) as most_used,

    (SELECT COUNT(*)
     FROM classroom_resource cr
     LEFT JOIN classroom c ON cr.room_num = c.room_num
     WHERE cr.res_room_status = '已预订'

         AND c.building_id = #{buildingId}


         AND c.room_type = #{roomType}

     GROUP BY cr.room_num
     ORDER BY COUNT(*) DESC
     LIMIT 1
    ) as most_usage_count,

    (SELECT CONCAT(b.building_name, '-', cr.room_num)
     FROM classroom_resource cr
     LEFT JOIN classroom c ON cr.room_num = c.room_num
     LEFT JOIN building b ON c.building_id = b.building_id
     WHERE cr.res_room_status = '已预订'

         AND c.building_id = #{buildingId}


         AND c.room_type = #{roomType}

     GROUP BY cr.room_num
     ORDER BY COUNT(*) ASC
     LIMIT 1
    ) as least_used,

    (SELECT COUNT(*)
     FROM classroom_resource cr
     LEFT JOIN classroom c ON cr.room_num = c.room_num
     WHERE cr.res_room_status = '已预订'

         AND c.building_id = #{buildingId}


         AND c.room_type = #{roomType}

     GROUP BY cr.room_num
     ORDER BY COUNT(*) ASC
     LIMIT 1
    ) as least_usage_count,

    (SELECT COUNT(*)
     FROM classroom_resource cr
     LEFT JOIN classroom c ON cr.room_num = c.room_num
     WHERE cr.res_room_status = '已预订'

         AND c.building_id = #{buildingId}


         AND c.room_type = #{roomType}

    ) as total_usage
